import machine as m# DEFINITIONS---WEMOS_D1 PIN <- ESP8266 PIN---#D0 = m.Pin(16,m.Pin.OUT)D5 = m.Pin(14,m.Pin.OUT)D2 = m.Pin(4,m.Pin.IN)D1 = m.Pin(5,m.Pin.OUT)led = m.Pin(2, m.Pin.OUT)'''DEFINE ADC'''sensor = m.ADC(0)class robo(object):  #  esta eh a classe robo - entidade composta por:  #  luz - corresponde a intensidade luminosa momentanea  #        na regiÃ£o que o robo ocupou recentemente.  #  mov - representa o par de comandos discretos capaz  #        acionar os motores do robo.      def __init__(self):    print('inicializando robo...')    # Definindo Sensor de Luminosidade    self.luz = sensor    # Definindos motores do robo    self.motor1 = m.PWM(D1,60, 0)     self.motor2 = m.PWM(D5,60, 0)   def anda(self):    # Ligando motores    self.motor1.duty(1024)    self.motor2.duty( 512)    print('andando...')      def para(self):    # Desligando motores    self.motor1.duty(0)    self.motor2.duty(0)    print('parando...')  def olha(self):    print('(entre 0 e 1024) a luz aqui eh --', self.luz )